function accAdd(h,g){var f,d,b,j;try{f=h.toString().split(".")[1].length}catch(i){f=0}try{d=g.toString().split(".")[1].length}catch(i){d=0}j=Math.abs(f-d);b=Math.pow(10,Math.max(f,d));if(j>0){var a=Math.pow(10,j);if(f>d){h=Number(h.toString().replace(".",""));g=Number(g.toString().replace(".",""))*a}else{h=Number(h.toString().replace(".",""))*a;g=Number(g.toString().replace(".",""))}}else{h=Number(h.toString().replace(".",""));g=Number(g.toString().replace(".",""))}return(h+g)/b}function accMul(d,b){var a=0,f=d.toString(),c=b.toString();try{a+=f.split(".")[1].length}catch(g){}try{a+=c.split(".")[1].length}catch(g){}return Number(f.replace(".",""))*Number(c.replace(".",""))/Math.pow(10,a)}function accSub(f,d){var c,b,a,h;try{c=f.toString().split(".")[1].length}catch(g){c=0}try{b=d.toString().split(".")[1].length}catch(g){b=0}a=Math.pow(10,Math.max(c,b));h=(c>=b)?c:b;return((f*a-d*a)/a).toFixed(h)}function accDiv(arg1,arg2){var t1=0,t2=0,r1,r2;try{t1=arg1.toString().split(".")[1].length}catch(e){}try{t2=arg2.toString().split(".")[1].length}catch(e){}with(Math){r1=Number(arg1.toString().replace(".",""));r2=Number(arg2.toString().replace(".",""));return(r1/r2)*pow(10,t2-t1)}}$(document).ready(function(){$("#sum").click(function(){var h=1.0903;var g=36;$("label").each(function(){if($(this).hasClass("am-active")){h=parseFloat($(this).attr("value"));g=parseFloat($(this).attr("yue"))}});var i=accAdd(2,accDiv(parseFloat($("#in").val()),100));var j=accSub(i,h);var f=parseInt($("#dai").val());var m=(Math.ceil(accMul(j,f)/100))*100;var l=accMul(m,h);var k=accDiv(l,g);$("#a1").html("");$("#b1").html("");$("#c1").html("");$("#d1").html("");$("#az").html("公式:");$("#a1").html(i+"-"+h+"="+j);$("#bz").html("分期金额(百位取整数):");$("#b1").html(f+"*"+j+"="+m);$("#cz").html("贷款总金额:");$("#c1").html(m+"*"+h+"="+l);$("#dz").html("月供:");$("#d1").html(l+"/"+g+"="+k)})});
